window.addEventListener("DOMContentLoaded",()=>{const e=document.querySelector(".navigation"),t=document.querySelectorAll(".navigation__link"),a=document.querySelector(".hamburger");a.addEventListener("click",()=>{a.classList.toggle("hamburger_active"),e.classList.toggle("navigation_active")}),t.forEach(t=>{t.addEventListener("click",()=>{a.classList.toggle("hamburger_active"),e.classList.toggle("navigation_active")})})});