window.addEventListener("DOMContentLoaded",()=>{const e=document.querySelector(".navigation"),t=document.querySelectorAll(".navigation__link"),i=document.querySelectorAll(".carousel-item_hide"),a=document.querySelector(".hamburger");a.addEventListener("click",()=>{a.classList.toggle("hamburger_active"),e.classList.toggle("navigation_active")}),t.forEach(t=>{t.addEventListener("click",()=>{a.classList.toggle("hamburger_active"),e.classList.toggle("navigation_active")})}),i.forEach(e=>{window.outerWidth<577&&(e.classList.remove("carousel-item_hide"),e.classList.add("carousel-item"))})});